import React, {FunctionComponent} from "react";
import Chat from "../models/Chat";
import "./chat-card.css";

type Props = {
    chat : Chat
    borderColor?:string
};

const ChatCard: FunctionComponent<Props> = ({chat,borderColor='#009688'}) => {

    return (
        <div className="col s6 m4">
            <div className="card horizontal" style={{borderColor: borderColor            }}>
                <div className="card-image">
                    <img src={chat.picture} alt={chat.name}/>
                </div>
                <div className="card-stacked">
                    <div className="card-content">
                        <p>{chat.name}</p>
                        <p><small>{chat.created.toString()}</small></p>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ChatCard;
